package com.zxb.myvueblog.demo.thread;import java.util.concurrent.Callable;import java.util.concurrent.ExecutionException;import java.util.concurrent.FutureTask;public class CallableTest {    public static  void main(String[] args) throws ExecutionException, InterruptedException {            Thread thread1 = new Thread1();            thread1.start();            Thread thread2 = new Thread(new Runnable1());            thread2.start();        FutureTask<String> futureTask = new FutureTask<>(new Callable1<>());        Thread thread3 = new Thread(futureTask);        thread3.start();        System.out.println(futureTask.get());    }    static class Thread1 extends  Thread{        @Override        public void run() {            System.out.println("Thread1...run....");        }    }    static  class  Runnable1 implements Runnable{        @Override        public void run() {            System.out.println("Thread2.....run.....");        }    }    static  class Callable1<T> implements Callable<T>{        @Override        public T call() throws Exception {            System.out.println("callable...call...");            return (T) new String("nihao");        }    }}